import React from "react";
import Chart from "../Chart/Chart";

function ExpensesChart(props){

    const chartDatePoints = [
        {label:"Jan", value:0},
        {label:"Feb", value:0},
        {label:"Mar", value:0},
        {label:"Apr", value:0},
        {label:"May", value:0},
        {label:"Jun", value:0},
        {label:"Jul", value:0},
        {label:"Aug", value:0},
        {label:"Sep", value:0},
        {label:"Oct", value:0},
        {label:"Nov", value:0},
        {label:"Dec", value:0}      
        
    ]

    for(const expense of props.expenses){ // Use expense of props.expenses instead of expense in props.expenses because props.expenses is an A
        const expenseMonth = expense.date.getMonth(); //starting at 0 => January => 0
        chartDatePoints[expenseMonth].value += expense.amount; //Here we can take advantage of the getMonth() retuning 0 - 11 , this will match the index range of the array chartDatePoints[expenseMonth]
    }

    return(
        <Chart dataPoints ={chartDatePoints}/>
    )
}

export default ExpensesChart;
